mapper.select:
		SELECT MEMBER_NO, MEMBER_ID, MEMBER_NM, MEMBER_PHONE,
        MEMBER_EMAIL, MEMBER_ADDR, MEMBER_INTEREST, MEMBER_GRADE
		FROM MEMBER
		WHERE MEMBER_ID = #{memberId} 
		AND MEMBER_PWD = #{memberPwd}
		AND MEMBER_STATUS = 'Y'

mapper.update:
UPDATE MEMBER
SET MEMBER_PHONE = #{memberPhone}, MEMBER_EMAIL = #{memberEmail}, MEMBER_ADDR = #{memberAddress}, MEMBER_INTEREST = #{memberInterest}
WHERE MEMBER_NO = #{memberNo}

mapper.select:
SELECT COUNT(*) FROM MEMBER
WHERE MEMBER_NO = #{memberNo}
AND MEMBER_PWD = #{memberPwd}

mapper.update:
UPDATE MEMBER
SET MEMBER_PWD = #{memberPwd}
WHERE MEMBER_NO = #{memberNo}

UPDATE MEMBER
SET MEMBER_STATUS = 'N'
WHERE MEMBER_NO = #{memberNo}
